.quotes {
  border-top: 2px solid grey;

  .container {
    position: relative;
    width: 80%;
    min-height: 80vh;
    flex-direction: column;
    justify-content: flex-start;
    padding: 2rem;
    margin: 0 auto;

    .title {
      background: rgba(240, 240, 240, 0.9);
      border-radius: 10px;
      width: 500px;
      text-align: center;
      padding: 2rem;

      p {
        color: $primary;
        border-bottom: 1px solid $primary;
        font-size: 1.5rem;
      }
    }

    .quote-area {
      // width: 100%;
      // height: 100%;
      display: grid;
      justify-content: center;
      margin-top: 3rem;
      grid-template-columns: repeat(3, 600px);
      grid-auto-rows: minmax(200px, auto);
      grid-gap: 2rem;

      .box {
        width: 600px;

        box-shadow: 2px 3px 10px;
        border-radius: 15px;
        .profile {
          flex-direction: column;
          padding: 1rem;

          img {
            border-radius: 50%;
            width: 100px;
            height: 100px;
          }

          p {
            font-size: 1.2rem;
            font-weight: 500;
            color: $primary;
          }
        }

        .quote {
          border-left: 2px solid $primary;
          padding: 1rem;

          p {
            font-size: 1.1rem;
          }
        }
      }
    }
    .go-top {
      position: absolute;
      right: 0;
      bottom: 0;
      padding: 2rem;

      i {
        font-size: 3rem;
        color: rgba(240, 240, 240, 0.9);
        transition: all 0.3s ease-in-out;

        &:hover {
          transform: scale(1.2);
          color: $third;
        }
      }
    }
  }
}

.box1 {
  grid-column: 2 / -1;
}
