.main {
  max-width: 100vw;
  display: flex;
  flex-direction: column;
  position: relative;
  overflow: hidden;

  @include phone {
    width: 100%;
    overflow: auto;
    height: auto;
  }

  & * {
    z-index: 10;
  }

  .slant {
    position: absolute;
    width: 1295px;
    height: 100%;
    left: 0;
    top: 0;
    background-color: white;
    transform: skewX(-20deg);
    z-index: 5;
    border-bottom: 1px solid $primary;

    &:after {
      content: "";
      position: absolute;
      top: 0;
      left: -100px;
      z-index: 6;
      width: 500px;
      height: 500px;
      background: white;
      transform: skewX(20deg);
    }

    @include phone {
      display: none;
    }

    @include tablet {
      width: 750px;
      left: -160px;
      transform: skewX(-25deg);
    }
  }

  header {
    position: relative;
    display: flex;
    justify-content: center;
    align-content: center;
    height: auto;
    max-width: 1375px;
    padding: 1.5rem;
    background: grey;
    border-bottom: 1px solid $primary;

    @include phone {
      padding: 0;
    }

    @include tablet {
      padding: 0;
    }

    .content {
      width: 90%;
      height: 100%;
      margin: 0 auto;
      display: flex;
      justify-content: space-between;

      @include phone {
        flex-direction: column;
        justify-content: center;
        align-items: center;
        padding: 0;
        width: 100%;
      }

      @include tablet {
        flex-direction: column;
        justify-content: center;
        align-items: center;
        width: 100%;
        padding: 1rem 0;
      }

      .logo {
        font-size: 2rem;
        color: $primary;
        font-weight: 600;
        border-bottom: 1px solid grey;
        box-shadow: 3px 5px 5px 5px black;
        padding: 0 1rem;
        transition: all 0.3s ease-in-out;

        &:hover {
          border-bottom: 1px solid $primary;
          box-shadow: 3px 5px 5px 5px $secondary;

          @include phone {
            box-shadow: none;
            border: none;
          }
        }

        @include phone {
          margin-top: 1rem;
          text-align: center;
          box-shadow: none;
          border: none;
        }

        @include tablet {
          width: 300px;
          text-align: center;
        }
      }

      .main-nav .nav-bar {
        padding: 1px;
        position: relative;
        display: flex;
        align-items: center;
        height: 65px;
        list-style-type: none;

        &:hover li .link {
          opacity: 0.2;
        }

        @include phone {
          justify-content: center;
          padding: 0;
        }

        @include tablet {
          width: auto;
          margin-top: 1rem;
        }

        li {
          .link {
            padding: 2rem;
            font-size: 1.1rem;
            transition: all 0.3s ease-in-out;
            color: $third;

            &:hover {
              color: $secondary;
              font-weight: 500;
              opacity: 1;
            }

            @include phone {
              font-size: 0.9rem;
              padding: 1.5rem;
            }

            @include tablet {
              padding: 1.5rem;
            }
          }
        }
      }

      &::after {
        content: "";
        top: -15px;
        right: -2px;
        position: absolute;
        transform-origin: bottom right;
        z-index: 5;
        width: 50px;
        height: 130px;
        background-color: grey;
        transform: rotate(20deg);

        @include phone {
          display: none;
        }

        @include tablet {
          display: none;
        }
      }
    }
  }

  .main-content {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-align: left;
    flex-grow: 1;
    height: auto;
    max-width: 1200px;

    .text {
      width: 80%;
      display: flex;
      justify-content: center;
      align-items: center;

      @include phone {
        margin: 0 auto;
        width: 90%;
        flex-direction: column;
        padding: 0;
        word-wrap: break-word;
        align-items: center;
        height: auto;
        text-align: center;
      }

      @include tablet {
        margin: 0;
        padding: 30px;
        width: 500px;
      }

      p {
        font-size: 1.3rem;
        color: $primary;
        line-height: 3.5rem;
        letter-spacing: 0.3rem;
        word-wrap: break-word;

        .bold {
          display: inline-block;
          margin-top: 1.5rem;
          color: $third;
          font-weight: 700;
          border-bottom: 1px solid white;
          transition: all 0.3s ease-in-out;
          letter-spacing: 0.4rem;

          @include phone {
            color: $third;
          }

          &:hover {
            color: $third;
            border-bottom: 1px solid $third;
          }
        }
      }

      ul {
        position: absolute;
        left: 180px;
        bottom: 0;
        display: flex;
        justify-self: end;
        margin-bottom: 4rem;

        &:hover li .link {
          opacity: 0.2;
        }

        @include phone {
          position: relative;
          justify-content: center;
          align-self: center;
          left: 0;
          margin: 2rem 0 1rem;
        }

        @include tablet {
          left: 30px;
          margin-bottom: 1rem;
          background: white;
          border-radius: 10px;
        }

        li .link {
          align-items: center;
          color: $primary;
          font-size: 1.1rem;
          transition: all 0.3s ease-in-out;
          padding: 1rem;
          border-bottom: 2px solid white;
          margin-right: 1.5rem;

          &:hover {
            opacity: 1;
            border-bottom: 2px solid rgb(144, 29, 29);
            color: rgb(144, 29, 29);
          }

          @include phone {
            font-size: 0.9rem;
            margin: 0;
            text-align: center;
          }

          @include tablet {
            font-size: 0.9rem;
            margin: 0;
            text-align: center;
            margin-left: -1rem;
            border: none;
          }
        }
      }
    }
  }

  img {
    position: absolute;
    bottom: -20px;
    right: -50px;
    max-width: 100%;
    width: auto;
    height: auto;
    z-index: 0;

    @include phone {
      display: none;
    }

    @include tablet {
      bottom: 0;
      height: 100%;
    }
  }
}

