.about {
  max-width: 100vw;
  position: relative;
  overflow: hidden;
  border-top: 2px solid rgb(100, 100, 100);

  &::before {
    content: "";
    width: 1200 px;
    height: 100%;
    transform: skewX(-20deg);
    position: absolute;
    top: 0;
    left: -250px;
    background-color: rgb(121, 117, 117);
    z-index: -1;

    @include phone {
      display: none;
    }

    @include tablet {
      display: none;
    }
  }

  @include phone {
    flex-direction: column;
  }

  & * {
    z-index: 100;
    position: relative;
  }

  .container {
    position: relative;
    flex-direction: column;
    width: 80%;
    margin: 4rem auto;
    margin-bottom: 7rem;
    background: rgba(240, 240, 240, 0.9);
    border-radius: 10px;
    padding: 4rem;

    @include phone {
      width: 100%;
      margin: 0;
      padding: 0;
      background: transparent;
    }

    @include tablet {
      width: 90%;
      margin: 0;
      padding: 0;
      background-color: transparent;
    }

    .title {
      background: white;
      border-radius: 10px;
      width: 500px;
      text-align: center;
      padding: 2rem;
      transition: all 0.3s ease-in-out;
      color: $primary;

      &:hover {
        background: rgb(100, 100, 100);
        color: $third;
      }

      &:hover h2 {
        border-color: $third;
      }

      h2 {
        font-size: 1.4rem;
        color: inherit;
        border-bottom: 1px solid $primary;
      }

      @include phone {
        width: 250px;
      }
    }

    .content {
      margin-top: 2rem;
      background: rgb(255, 255, 255);
      padding: 2rem;
      border-radius: 15px;

      @include phone {
        flex-direction: column;
        margin-top: 0;
      }

      @include tablet {
        padding: 0;
      }

      .img-container {
        .img {
          height: 500px;
          width: 1000px;
          border-radius: 10px;

          @include phone {
            margin-bottom: 2rem;
            height: 300px;
            width: 300px;
          }

          @include tablet {
            margin-bottom: 2rem;
            height: 300px;
            width: 300px;
          }
        }
      }
      .about-info {
        width: 100%;

        .info {
          padding: 1rem;
          margin-left: 1.5rem;
          border-left: 1px solid $primary;

          @include phone {
            margin-left: 1rem;
          }
        }
      }
    }
  }

  .go-top {
    position: absolute;
    right: 0;
    bottom: 0px;
    margin: 2rem;
    border-radius: 20px 0 0 0;
    background: transparent;

    i {
      font-size: 3rem;
      color: rgb(207, 207, 207);
      transition: all 0.3s ease-in-out;

      &:hover {
        transform: scale(1.2);
        color: $third;
      }

      @include phone {
        font-size: 2.5rem;
      }
    }

    @include phone {
      position: relative;
    }
  }
}

