footer {
  position: relative;
  width: 100%;
  height: 20vh;

  @include phone {
    height: auto;
    padding: 1rem 0;
  }

  .img {
    position: absolute;
    top: 0;
    left: 0;
    z-index: 0;
    width: 100%;
    height: 100%;
    max-width: auto;
  }

  &:after {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);
    z-index: 1;
  }

