    align-items: flex-start;
    justify-content: center;
    text-align: center;

    .location {
      margin-right: 2rem;
      flex-direction: column;

      p {
        font-size: 1.3rem;
        border-bottom: 1px solid black;
      }

      .text {
        margin-top: 1.5rem;
        font-size: 1.1rem;

        @include phone {
          margin-top: 1rem;
        }
      }

      @include phone {
        margin: 0;
        margin-bottom: 1rem;
      }

      @include tablet {
        margin: 0;
        margin-bottom: 1.2rem;
        padding: 1rem;
      }
    }

    .social {
      margin-left: 2rem;
      flex-direction: column;

      p {
        font-size: 1.3rem;
        border-bottom: 1px solid black;
      }

      @include phone {
        margin: 0;
      }

      .icons a {
        margin-top: 1rem;
        color: black;
        font-size: 2rem;
        transition: all 0.3s ease-in-out;
        padding: 0 0.8rem;

        &:hover {
          color: $third;
        }

        @include phone {
          font-size: 1.5rem;
        }
      }

      @include tablet {
        margin: 0;
        padding: 1rem;
      }
    }

    @include phone {
      padding-top: 1rem;
      flex-direction: column;
      align-items: center;
    }
  }

  & * {
    z-index: 100;
  }
}

